import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import macarrao from '../../imagens/Macarrao com camarão 1.png'; 
import './maisvendidos.css';


const slides = [
    {
      title: "Espaguete com camarão",
      description: "Suculentos camarões com infusão de manteiga e alho escaldantes em um saboroso molho de creme, misturados com macarrão perfeitamente cozido.",
      restaurant: "Olive Garden"
    },
    // Adicione mais pratos aqui se necessário
];

function MaisVendidos(){
    return (
        <section className="sec5__maisVendidos">
            <h2>Pratos mais vendidos</h2>
            <div className="sec5__pratos">
                <Swiper
                    spaceBetween={50}
                    slidesPerView={3}
                    onSlideChange={() => console.log('slide change')}
                    onSwiper={(swiper) => console.log(swiper)}
                >
                    {slides.map((slide, index) => (
                        <SwiperSlide key={index} className="sec5__slide">
                            <img src={macarrao} className="slide__imagem" alt="Prato de espaguete" />
                            <div className="container__slide">
                                <h3>{slide.title}</h3>
                                <h4>{slide.description}</h4>
                                <p>{slide.restaurant}</p>
                                <a href="#">Saiba mais</a>
                            </div>
                        </SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </section>
    );
}

export default MaisVendidos;
