import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import './maisvendidos.css';
import React, { useState, useEffect } from 'react';



function MaisVendidos(){
    const [slides, setSlides] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const response = await fetch('./pratos.json');
            const data = await response.json();
            setSlides(data);
        };

        fetchData();
    }, []);



    return (
        <section className="sec5__maisVendidos">
            <h2>Pratos mais vendidos</h2>
            <div className="sec5__pratos">
                <Swiper>
                    {slides.map((slide, index) => (
                        <SwiperSlide key={index} className="sec5__slide">
                            <img src={slide.imagem} className="slide__imagem" alt="Prato de espaguete" />
                            <div className="container__slide">
                                <h3>{slide.title}</h3>
                                <h4>{slide.description}</h4>
                                <p>{slide.restaurant}</p>
                                <a href="#">Saiba mais</a>
                            </div>
                        </SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </section>
    );
}

export default MaisVendidos;
